import requestsdef ok(response):    if response:        json_response = response.json()        name = json_response["response"]["GeoObjectCollection"]["metaDataProperty"]["GeocoderResponseMetaData"]["request"]        ok1 = json_response["response"]["GeoObjectCollection"]["featureMember"][0]["GeoObject"]        ok2 = ok1["metaDataProperty"]["GeocoderMetaData"]["Address"]        ok3 = ok2["Components"][1]["name"]        print(name, "-", ok3)    else:        print("Ошибка выполнения запроса:")        print(geocoder_request)        print("Http статус:", response.status_code, "(", response.reason, ")")list = ['Хабаровск', 'Уфа', 'Нижний Новгород', 'Калининград']for city in list:    geocoder_request = "http://geocode-maps.yandex.ru/1.x/?apikey=40d1649f-0493-4b70-98ba-98533de7710b&geocode={}&format=json".format(city)    response = requests.get(geocoder_request)    ok(response)